import React, { useEffect, useState } from "react";
import { Card, CardContent, Button, Input} from "antd";


const ListingsPage = () => {
  const [activeCategory, setActiveCategory] = useState("cars");
  const [searchQuery, setSearchQuery] = useState("");
  const [listings, setListings] = useState({ cars: [], carSales: [], trips: [], accessories: [] });

  useEffect(() => {
    // Fetch all categories data from the backend
    const fetchListings = async () => {
      try {
        const [cars, carSales, trips, accessories] = await Promise.all([
          fetch("/cars").then((res) => res.json()),
          fetch("/car-sales").then((res) => res.json()),
          fetch("/trips").then((res) => res.json()),
          fetch("/accessories").then((res) => res.json()),
        ]);
        setListings({ cars, carSales, trips, accessories });
      } catch (error) {
        console.error("Error fetching listings:", error);
      }
    };
    fetchListings();
  }, []);

  const handleDelete = async (category, id) => {
    try {
      await fetch(`/${category}/${id}`, { method: "DELETE" });
      setListings((prev) => ({
        ...prev,
        [category]: prev[category].filter((item) => item.id !== id),
      }));
    } catch (error) {
      console.error("Error deleting item:", error);
    }
  };

  const renderListings = () => {
    const filteredListings = listings[activeCategory].filter((item) =>
      Object.values(item).some((value) =>
        value.toString().toLowerCase().includes(searchQuery.toLowerCase())
      )
    );

    return (
      <div>
        {filteredListings.map((item) => (
          <Card key={item.id} className="mb-4">
            <CardContainer>
              <div className="flex justify-between items-center">
                <div>
                  {Object.entries(item).map(([key, value]) => (
                    <p key={key}>
                      <strong>{key}:</strong> {value}
                    </p>
                  ))}
                </div>
                <div>
                  <Button
                    className="mr-2"
                    onClick={() => console.log("Edit", item.id)}
                  >
                    Edit
                  </Button>
                  <Button
                    variant="destructive"
                    onClick={() => handleDelete(activeCategory, item.id)}
                  >
                    Delete
                  </Button>
                </div>
              </div>
            </CardContainer>
          </Card>
        ))}
      </div>
    );
  };

  return (
    <div className="p-4">
      <div className="flex space-x-4 mb-4">
        <Button onClick={() => setActiveCategory("cars")}>Cars</Button>
        <Button onClick={() => setActiveCategory("carSales")}>Car Sales</Button>
        <Button onClick={() => setActiveCategory("trips")}>Trips</Button>
        <Button onClick={() => setActiveCategory("accessories")}>Accessories</Button>
      </div>
      <Input
        placeholder="Search by name"
        value={searchQuery}
        onChange={(e) => setSearchQuery(e.target.value)}
        className="mb-4"
      />
      {renderListings()}
    </div>
  );
};

export default ListingsPage;
